
public class SwitchDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int choice = 2;
		
		switch(choice) {
			
		case 1:
			System.out.println("1");
			break;
			
		case 2:
			System.out.println("2");
			break;
			
		case 3:
			System.out.println("3");
			break;
			
		default:
			System.out.println("default");
		}
	}

}
